import { Button, BaseControl } from "@wordpress/components";
import { ComponentBlock } from "/lib/block-utils";

export const block = {
  apiVersion: 2,
  title: "Button",
  icon: "smiley",
  category: "components",
  attributes: {
    variant: {
      type: "string",
      enum: ["primary", "secondary", "tertiary"],
      default: null,
      control: true
    },
    content: {
      type: "string",
      default: "Button"
    }
  }
};

export const ButtonBlock = ({ children, ...props }) => (
  <ComponentBlock component={Button} schema={block}>
    {children}
  </ComponentBlock>
);

export default {
  ...block,
  edit: ButtonBlock
};
