import {
  SelectControl,
  __experimentalInputControl as InputControl
} from "@wordpress/components";
import { InspectorControls, useBlockProps } from "@wordpress/block-editor";
import { useEffect, useState } from "@wordpress/element";

const defaultChangeHandler = (value, { setValue }, prev) => setValue(value);

const CONTROL_PROP_TRANSFORMS = [
  [InputControl, (props, schema) => props],
  [
    SelectControl,
    (props, schema) => {
      if (props.options) {
        if (!Array.isArray(props.options)) {
          props.options = Object.entries(
            props.options
          ).map(([label, value]) => ({ label, value }));
        }
      } else {
        if (schema.enum) {
          props.options = schema.enum.map((value) => ({
            value,
            label: value[0].toUpperCase() + value.substr(1)
          }));
        }

        if (!props.required) {
          props.options.unshift({
            value: null,
            label: "(none)"
          });
        }
      }

      if (!props.value)
        props.value = schema.default ?? props.options[0]?.value ?? null;

      return props;
    }
  ]
];

const useRepo = (defaults = {}) => {
  const [keys, setKeys] = useState(
    Array.isArray(defaults)
      ? defaults.map(([key]) => key)
      : Object.keys(defaults)
  );
  const [values, setValues] = useState(
    Array.isArray(defaults)
      ? defaults.map((item) => item.value)
      : Object.values(defaults)
  );

  return useMemo(
    () =>
      new Proxy(
        {
          items: {},

          has: (key) =>
            typeof key === "number"
              ? values[key] !== undefined
              : keys.includes(key),

          includes: (value) => values.includes(value),

          indexOf: (value) => values.indexOf(value),

          get: (key) =>
            typeof key === "number" ? values[key] : values[keys.indexOf(key)],

          set: (key, value) => {
            const index = typeof key === "number" ? key : keys.indexOf(key);

            if (index > 0) {
              setValues(values.map((v, i) => (i === index ? value : v)));
            } else {
              setValues(values.concat(value));
              setKeys(keys.concat(key));
            }

            return value;
          },

          add: (key_object_entries, value) => {
            if (["string", "number"].includes(typeof key_object_entries)) {
              if (this.has(key_object_entries)) return;

              return this.set(key_object_entries, value);
            }

            if ("object" === typeof key_object_entries) {
              if (!(key_object_entries instanceof Array))
                key_object_entries = Object.entries(key_object_entries);

              return key_object_entries.map(([key, v]) => this.add(key, v));
            }

            throw new TypeError("Unknown argument type");
          },

          *[Symbol.iterator]() {
            for (let i = 0; i < keys.length; i++) yield [keys[i], values[i]];
          }
        },
        {
          get: (api, prop, receiver) => {
            if (api.hasOwnProperty(prop)) return api[prop];

            switch (prop) {
              case "length":
                return keys.length;

              default:
                return api.get(prop);
            }
          },
          set: (api, prop, value) =>
            api.hasOwnProperty(prop)
              ? (api[prop] = value)
              : api.set(prop, value)
        }
      ),
    [keys, values]
  );
};

const useTransforms = (Component, schema) => {
  const registry = useRepo(CONTROL_PROP_TRANSFORMS);
  const [queue, setQueue] = useState([]);

  useEffect(() => {
    const tx = [];

    if (registry[Component] && !queue.includes(registry[Component]))
      tx.push([...queue, registry[Component]]);

    if (tx.length) setQueue([...queue, ...tx]);
  }, [available_transforms, Component, schema]);

  const api = {
    registry,
    queue,
    add: (key) => {
      const transform = registry[key];

      if (transform) {
        if (!queue.includes(transform)) setQueue([...queue, transform]);

        return;
      }

      if ("function" === typeof key) {
        registry.add(key, key);

        setQueue([...queue, key]);

        return;
      }

      throw new TypeError("Unknown key type.");
    },
    register: registry.add.bind(registry),
    apply(props = {}) {
      for (const transform of queue) props = transform(props, schema);

      return props;
    }
  };

  return api;
};

const useControlProps = (Component, schema, props = {}) => {
  const transforms = useTransforms(Component, schema);
  const [control_props, setControlProps] = useState(props);

  useEffect(() => {
    setControlProps(transforms.apply(props));
  }, [transforms, props]);

  return {
    control_props,
    transforms,
    setControlProps
  };
};

export const BlockControl = ({ name, schema, val }) => {
  const [control, setControl] = useState(schema.control ?? {});
  const [required, setRequired] = useState(schema.required ?? false);
  const [value, setValue] = useState(val ?? schema.default);
  const [Component, setComponent] = useState();
  const { control_props, setControlProps } = useControlProps(
    Component,
    schema,
    {
      key: name,
      value,
      required
    }
  );
  const [onChange, setOnChange] = useState(
    control_props.onChange ?? schema.onChange ?? defaultChangeHandler
  );

  useEffect(() => {
    if (Component || !schema.control) return;

    if (schema.control.component) setComponent(schema.control.component);

    if (schema.type === "string")
      setComponent(schema.enum ? SelectControl : InputControl);
  }, [Component, schema]);

  return (
    <Component
      {...control_props}
      onChange={(v) => onChange(v, setValue, value)}
    />
  );
};

export const useBlockControls = (schemas, props = {}) => {
  const [controls, setControls] = useState([]);

  useEffect(() => {
    const controls = [];

    for (const [name, schema] of Object.entries(schemas))
      controls[name] = <BlockControl name={name} schema={schema} {...props} />;

    setControls(controls);
  }, [schemas, props]);

  controls.set = setControls;

  return controls;
};

export const ComponentBlock = ({
  component: Component,
  schema,
  children,
  ...props
}) => (
  <div {...useBlockProps()}>
    <InspectorControls>{useBlockControls(schema)}</InspectorControls>
    <Component {...props}>{children}</Component>
  </div>
);
