import { useEffect, useState } from "@wordpress/element";

import "./style.scss";

const loadScenes = async (...names) =>
  Promise.all(names.map((name) => import(`/scenes/${name}`)));

export function App() {
  //const { scenes } = useScenes("BlockEditor");
  const scenes = {};

  return (
    <div className="app">
      <header>
        <h2>Berlin Editor</h2>
      </header>
      {Object.values(scenes)}
    </div>
  );
}

export default App;

