import { createSlice } from "@reduxjs/toolkit";

export const activeSceneName = createSlice({
  name: "activeScene",
  initialState: null,
  reducers: {
    setActiveSceneName: (state, action) => action.payload
  }
});

export const selectors = {
  getActiveSceneName: (state) => state.root.active_scene_name
};

export const {
  name,
  actions,
  caseReducer,
  reducer,
  slice = activeSceneName
} = activeSceneName;
